// DetailsPage.js
import React from "react";
import { useParams } from "react-router-dom"; // Import useParams to access the URL parameters

const showDetails = {
  arijit: {
    img: require('../images/arijit.png'),
    title: "Arijit Singh Live Concert",
    date: "November 25, 2024",
    time: "7:00 PM",
    venue: "XYZ Arena",
    ageLimit: "16+",
    duration: "3 hours",
    description: "Experience the soulful melodies of Arijit Singh live, as he brings his mesmerizing voice to the stage, captivating audiences with his heartfelt performances.",
  },
  neha: {
    img: require('../images/neha.png'),
    title: "Neha Kakkar Live Concert",
    date: "December 5, 2024",
    time: "8:00 PM",
    venue: "ABC Stadium",
    ageLimit: "All Ages",
    duration: "2 hours",
    description: "Get ready to groove with Neha Kakkar's electrifying live performance, filled with her chart-topping hits and infectious energy.",
  },
  // Add other show details similarly
};

const DetailsPage = () => {
  const { showId } = useParams(); // Get the showId from the URL
  const show = showDetails[showId]; // Get the specific show details

  if (!show) {
    return <div className="text-center text-white">Show not found</div>;
  }

  return (
    <div className="bg-black min-h-screen text-white p-6">
      <div className="max-w-3xl mx-auto bg-slate-900 p-6 rounded-lg shadow-lg">
        <img src={show.img} alt={show.title} className="w-full h-64 object-cover rounded-lg mb-6" />
        <h2 className="text-3xl font-bold text-violet-400 mb-4">{show.title}</h2>
        <p className="text-lg font-serif text-violet-200 mb-2">
          <strong>Date:</strong> {show.date}
        </p>
        <p className="text-lg font-serif text-violet-200 mb-2">
          <strong>Time:</strong> {show.time}
        </p>
        <p className="text-lg font-serif text-violet-200 mb-2">
          <strong>Venue:</strong> {show.venue}
        </p>
        <p className="text-lg font-serif text-violet-200 mb-2">
          <strong>Age Limit:</strong> {show.ageLimit}
        </p>
        <p className="text-lg font-serif text-violet-200 mb-2">
          <strong>Duration:</strong> {show.duration}
        </p>
        <p className="text-lg font-serif text-violet-200 mt-4">
          {show.description}
        </p>
        <div className="mt-6">
          <button className="bg-violet-500 text-white px-4 py-2 rounded-lg hover:bg-violet-600 transition duration-200">
            Buy Tickets
          </button>
        </div>
      </div>
    </div>
  );
};

export default DetailsPage;
