import React from "react";
import { Link } from "react-router-dom";
import img1 from '../images/arijit2.jpg';
import img2 from '../images/armaan.jpg';
import img3 from '../images/akash.jpg';
import img4 from '../images/shashi.jpg';
import img5 from '../images/aujla.png';
import img6 from '../images/bassi.jpg';

const HomePage = () => {
  return (
    <div className="bg-black min-h-screen text-white">
      <div className="finished-shows">
        <div className="flex flex-wrap justify-center w-11/12 m-auto gap-4 mt-4">
          {/* First Row */}
          {[img1, img2, img3].map((img, index) => (
            <div key={index} className="w-80 bg-slate-900 text-white rounded-lg mb-4">
              <img src={img} alt={`Concert ${index + 1}`} className="w-full h-auto rounded-t-lg" />
              <div className="p-2">
                <div className="title text-violet-400 font-serif text-xl mb-1">
                  Concert {String.fromCharCode(65 + index)} - Concert Title
                </div>
                <div className="desc font-serif text-violet-200 mb-2">
                  Description for Concert {String.fromCharCode(65 + index)}.
                </div>
                <div className="flex justify-center">
                  <Link to="/detailsFinished" className="bg-black text-violet-300 p-2 font-serif rounded-xl details-button">
                    Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="flex flex-wrap justify-center w-11/12 m-auto gap-4 mb-4">
          {/* Second Row */}
          {[img4, img5, img6].map((img, index) => (
            <div key={index} className="w-80 bg-slate-900 text-white rounded-lg mb-4">
              <img src={img} alt={`Concert ${index + 4}`} className="w-full h-auto rounded-t-lg" />
              <div className="p-2">
                <div className="title text-violet-400 font-serif text-xl mb-1">
                  Concert {String.fromCharCode(68 + index)} - Concert Title
                </div>
                <div className="desc font-serif text-violet-200 mb-2">
                  Description for Concert {String.fromCharCode(68 + index)}.
                </div>
                <div className="flex justify-center">
                  <Link to="/detailsFinished" className="bg-black text-violet-300 p-2 font-serif rounded-xl details-button">
                    Details
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
